class Solution:
	def maxAlternatingSum(self, nums):
		#base case when array is empty or has only one element
		if len(nums) < 2:
			return 0
		#initialize variables to keep track of alternating sum and maximum sum found so far
		altern_sum = [0]*len(nums)
		max_sum = -float('inf')

		for i in range(1, len(nums)):
			altern_sum[i] = nums[i-1] - (nums[i-1] % 2 == 0) * ((i-1) // 2 + 1)

	#compare the absolute value of alternating sum to find maximum sum
		for i in range(len(nums)-2, -1, -1): 
			max_sum = max(max_sum, abs(altern_sum[i]))
			
		return max_sum
